#ifndef EXEC_TYPES_H
#include <exec/types.h>
#endif

#define BUGREPORT_TAGBASE          0x8EDA0000

/* Boolean tag, TRUE means bugreport is generated automatically, FALSE means  */
/* bug is created on user request (different info text is displayed). Default */
/* is TRUE.                                                                   */

#define BUGREPORT_AUTOGENERATED    (BUGREPORT_TAGBASE + 1)

/* Boolean tag controlling message buffer clearing. If set to FALSE, message  */
/* buffer (holding results of previous BPrintf() and BPutStr() calls) is not  */
/* cleared, and may be reused with following SendBug() calls (for example     */
/* with different e-mail address. Defaults to TRUE (clear buffer).            */

#define BUGREPORT_CLEAR            (BUGREPORT_TAGBASE + 2)

/* Boolean tag controlling inclusion of the complete list of processes into   */
/* the report. Defaults to FALSE. (v50.11)                                    */

#define BUGREPORT_ADD_PROCLIST     (BUGREPORT_TAGBASE + 3)

/* Boolean tag controlling inclusion of listing of connected USB devices into */
/* the report. Defaults to FALSE. (v50.11)                                    */

#define BUGREPORT_ADD_USB          (BUGREPORT_TAGBASE + 4)

/* Boolean tag controlling inclusion of display information into the report.  */
/* Defaults to FALSE. (v50.11)                                                */

#define BUGREPORT_ADD_DISPLAY      (BUGREPORT_TAGBASE + 5)


#endif /* LIBRARIES_BUGREPORT_H */
